,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH  rai_ids AS',
'( SELECT rai_id FROM SMS_GEBRUIKER_RACI WHERE gbr_id = sms_pck.sms_gebruikers_id ( p_windows_user => NVL(V(''APP_USER''),USER)) )',
'SELECT status',
',      sms_pck.functie_naam(rai_id_beheerder) beheerder',
',      sms_pck.functie_naam(rai_id_eigenaar) eigenaar',
',      sms_pck.functie_naam(rai_id_uitvoerder) uitvoerder',
',      jaar',
',      bron',
',      COUNT(*) AANTAL',
'FROM (',
'SELECT dct.rai_id rai_id_beheerder',
',      dct.rai_id_eigenaar',
',      ace.status',
',      ace.rai_id rai_id_uitvoerder',
',      EXTRACT (YEAR FROM ace.datum_uitvoering) Jaar',
',      ''DOCUMENT'' bron',
'FROM   sms_documenten dct',
',      sms_acties ace',
'WHERE  dct.id = ace.dct_id',
'AND    ( dct.rai_id IN ( SELECT rai_id FROM rai_ids) OR dct.rai_id_eigenaar IN ( SELECT rai_id FROM rai_ids) OR ace.rai_id IN ( SELECT rai_id FROM rai_ids))',
'AND    ace.status != ''Afgerond''',
'UNION ALL',
'SELECT obw.rai_id_beheerder',
',      obw.rai_id_eigenaar',
',      ace.status',
',      NVL(ace.rai_id,cte.rai_id_resp_werking) rai_id_uitvoerder',
',      EXTRACT (YEAR FROM ace.datum_uitvoering) Jaar',
',      ''CONTROLE'' bron',
'FROM   sms_controles cte',
',      sms_obw obw',
',      sms_acties ace',
'WHERE  cte.id = ace.cte_id',
'AND    obw.id = cte.obw_id',
