,p_plug_display_point=>'BODY_3'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT mt',
',      tl',
',      COUNT(DISTINCT naam) naam',
',      SUM(actie) acties',
',      SUM(behandeling) behandeling',
',      SUM(Gepland) gepland',
',      SUM(hersteld) hersteld',
',      SUM(nok) nok',
'FROM   ( SELECT dct.id',
'         ,      dct.naam',
'         ,      sms_pck.functie_naam (dct.rai_id_eigenaar) mt',
'         ,      sms_pck.functie_naam (dct.rai_id) tl',
'         ,      1 actie',
'         ,      DECODE(ate.status,''In behandeling'',1,0) behandeling',
'         ,      DECODE(ate.status,''Gepland'',1,0) gepland',
'         ,      DECODE(ate.AFWIJKINGEN_INDICATOR,''J'',1,0) nok',
'         ,      DECODE(ate.AFWIJKING_HERSTELD_INDICATOR,''J'',1,0) Hersteld',
'         FROM   sms_documenten dct',
'         ,      sms_acties ate',
'         WHERE  ate.dct_id        = dct.id',
'         AND    dct.status       != ''VERVALLEN''',
'         AND    ate.status       != ''Afgerond''',
'         AND    ate.datum_uitvoering >= NVL(:P9_DATUM_INGANG,ate.datum_uitvoering)',
'         AND    ate.datum_uitvoering <= NVL(:P9_DATUM_EINDE,ate.datum_uitvoering)',
'         UNION ALL',
'         ( SELECT dct.id',
'           ,      dct.naam',
'           ,      sms_pck.functie_naam (dct.rai_id_eigenaar) mt',
'           ,      sms_pck.functie_naam (dct.rai_id) tl',
'           ,      0 actie',
'           ,      0',
'           ,      0',
'           ,      0',
'           ,      0',
'           FROM   sms_documenten dct',
'           WHERE  ( dct.datum_ingang <= NVL(:P9_DATUM_EINDE,dct.datum_ingang)  OR dct.datum_ingang IS NULL)',
'           AND    ( dct.datum_einde >= NVL(:P9_DATUM_INGANG,dct.datum_einde)  OR dct.datum_einde IS NULL)',
'           AND    dct.status       != ''VERVALLEN''',
'           MINUS',
'           SELECT dct.id',
'           ,      dct.naam',
'           ,      sms_pck.functie_naam (dct.rai_id_eigenaar) mt',
'           ,      sms_pck.functie_naam (dct.rai_id) tl',
'           ,      0 actie',
'           ,      0',
'           ,      0',
'           ,      0',
'           ,      0',
'           FROM   sms_documenten dct',
'           ,      sms_acties ate',
'           WHERE  ate.dct_id        = dct.id',
'--           AND    ate.status       != ''Afgerond''',
'--           AND    dct.status       != ''VERVALLEN''',
'           AND    ate.datum_uitvoering >= NVL(:P9_DATUM_INGANG,ate.datum_uitvoering)',
'           AND    ate.datum_uitvoering <= NVL(:P9_DATUM_EINDE,ate.datum_uitvoering)',
'         )',
'       )',
'GROUP BY mt, tl',
''))
