wwv_flow_api.create_page_plug(
 p_id=>wwv_flow_api.id(18430902279701410966)
,p_plug_name=>'Voortgang'
,p_region_template_options=>'#DEFAULT#'
,p_component_template_options=>'#DEFAULT#'
,p_plug_template=>wwv_flow_api.id(23153220512016127539)
,p_plug_display_sequence=>20
,p_include_in_reg_disp_sel_yn=>'N'
,p_plug_new_grid_row=>false
,p_plug_new_grid_column=>false
,p_plug_display_column=>1
,p_plug_display_point=>'BODY_3'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT mt',
',      tl',
',      COUNT(DISTINCT bvg_id) bevinding',
',      SUM(actie) acties',
',      SUM(rapportage) rapportage',
',      SUM(behandeling) behandeling',
',      SUM(hersteld) hersteld',
',      SUM(nok) nok',
'FROM   ( WITH bevindingen AS ',
'         (      SELECT bvg.id bvg_id',
'         ,      sms_pck.functie_naam (obw.rai_id_eigenaar) mt',
'         ,      sms_pck.functie_naam (obw.rai_id_beheerder) tl',
'         ,      1 actie',
'         ,      DECODE(ate.status,''Afgerond'',1,0) rapportage',
'         ,      DECODE(ate.status,''In behandeling'',1,0) behandeling',
'         ,      DECODE(ate.AFWIJKINGEN_INDICATOR,''J'',1,0) nok',
'         ,      DECODE(ate.AFWIJKING_HERSTELD_INDICATOR,''J'',1,0) Hersteld',
'         FROM   sms_obw obw',
'         ,      sms_acties ate',
'         ,      sms_bevindingen bvg',
'         WHERE  bvg.obw_id        = obw.id',
'         AND    ate.bvg_id        = bvg.id',
'         AND    ate.datum_uitvoering >= NVL(:P14_DATUM_INGANG,ate.datum_uitvoering)',
'         AND    ate.datum_uitvoering <= NVL(:P14_DATUM_EINDE,ate.datum_uitvoering)',
'         AND ( :P14_TE_LAAT IS NULL OR ( bvg.datum_gereed < SYSDATE AND bvg.gereed_ind=''N'' ))',
'         AND ( :P14_DEADLINE IS NULL OR (((bvg.datum_gereed - SYSDATE) BETWEEN 0 AND 7) AND bvg.gereed_ind=''N'' ))',
'         )         ',
'         SELECT * FROM bevindingen',
'         UNION ALL',
'         SELECT bvg.id bvg_id',
'         ,      sms_pck.functie_naam (obw.rai_id_eigenaar) mt',
'         ,      sms_pck.functie_naam (obw.rai_id_beheerder) tl',
'         ,      0 actie',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         FROM   sms_obw obw',
'         ,      sms_bevindingen bvg',
'         WHERE  ( obw.datum_ingang <= NVL(:P14_DATUM_EINDE,obw.datum_ingang)  OR obw.datum_ingang IS NULL)',
'         AND    ( obw.datum_einde >= NVL(:P14_DATUM_INGANG,obw.datum_einde)  OR obw.datum_einde IS NULL)',
'         AND    bvg.obw_id = obw.id',
'         AND ( :P14_TE_LAAT IS NULL OR ( bvg.datum_gereed < SYSDATE AND bvg.gereed_ind=''N'' ))',
'         AND ( :P14_DEADLINE IS NULL OR (((bvg.datum_gereed - SYSDATE) BETWEEN 0 AND 7) AND bvg.gereed_ind=''N'' ))',
'         AND    bvg.id NOT IN ( SELECT bvg_id FROM bevindingen)',
'       )',
'GROUP BY mt, tl',
'',
''))
,p_plug_source_type=>'NATIVE_IR'
,p_plug_query_row_template=>1
);
