,p_plug_display_column=>1
,p_plug_display_point=>'BODY_3'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT bron',
',      uitvoerder',
',      eigenaar',
',      beheerder',
',      jaar',
',      status',
',      acties',
',      COUNT(*) aantal',
'FROM ( SELECT bron',
'       ,      uitvoerder',
'       ,      DECODE(NVL(obw_id,-1),-1,sms_pck.bepaal_doc_eigenaar(dct_id),sms_pck.bepaal_obw_eigenaar( p_obw_id => obw_id)) eigenaar',
'       ,      DECODE(NVL(obw_id,-1),-1,sms_pck.bepaal_doc_beheerder(dct_id),sms_pck.bepaal_obw_beheerder( p_obw_id => obw_id)) beheerder',
'       ,      jaar',
'       ,      status',
'       ,      ''Acties'' acties',
'       FROM   ( SELECT sms_pck.herkomst_type(t.cte_id,t.dct_id,t.bvg_id) bron',
'                ,      sms_pck.functie_naam(t.rai_id) uitvoerder',
'                ,      dct_id',
'                ,      sms_pck.bepaal_maatregel(bvg_id,dct_id,cte_id) obw_id',
'                ,      EXTRACT (YEAR FROM t.datum_uitvoering) Jaar',
'                ,      t.status',
'                ,      ( SELECT sms_pck.obw_pcs_ids( pin_obw_id => cte.obw_id) pcs_ids',
'                         FROM   sms_controles cte',
'                         WHERE  cte.id = t.cte_id',
'                       ) obw_pcs_ids',
'                ,      ( SELECT sms_pck.obw_pcs_ids( pin_obw_id => bvg.obw_id) pcs_ids',
'                         FROM   sms_bevindingen bvg',
'                         WHERE  bvg.id = t.bvg_id',
'                       ) bvg_pcs_ids',
'                ,      ( SELECT pcs_id',
'                         FROM   sms_controles cte',
'                         WHERE  cte.id = t.cte_id',
'                       ) cte_pcs_id',
'                ,      ( SELECT RTRIM (XMLAGG (XMLELEMENT (e, sms_pck.obw_pcs_ids( pin_obw_id => dow.obw_id) || '','')).EXTRACT (''//text()''), '','')',
'                         FROM   sms_documenten_obw dow',
'                         WHERE  dow.dct_id = t.dct_id',
'                       ) dct_pcs_ids',
'                ,      CASE WHEN t.status != ''Afgerond'' AND datum_uitvoering < SYSDATE THEN ''J'' ELSE ''N'' END te_laat',
'                ,      CASE WHEN t.status != ''Afgerond'' AND ((datum_uitvoering - SYSDATE) BETWEEN 0 AND 7) THEN ''J'' ELSE ''N'' END deadline',
'                FROM   sms_acties t   ',
'                WHERE ( :P24_DATUM_UITVOERING_VAN IS NULL',
'                        OR ',
'                        datum_uitvoering>=:P24_DATUM_UITVOERING_VAN',
'                      )',
'                AND   ( :P24_DATUM_UITVOERING_TOT IS NULL',
'                        OR ',
'                        datum_uitvoering<=:P24_DATUM_UITVOERING_TOT',
'                      )',
'                AND   ( :P24_STATUS IS NULL',
'                        OR ',
'                        status =:P24_STATUS',
'                      )',
'                AND   ( :P24_RAI_ID_UITVOERDER IS NULL',
'                        OR ',
'                        rai_id =:P24_RAI_ID_UITVOERDER',
'                      )',
'                AND   ( :P24_AFWIJKINGEN_INDICATOR IS NULL',
'                        OR',
'                        afwijkingen_indicator = :P24_AFWIJKINGEN_INDICATOR',
'                      )',
'              )',
'       WHERE ( :P24_JAAR IS NULL',
'               OR    ',
'               :P24_JAAR = jaar',
'             )',
'       AND   ( :P24_BRON IS NULL',
'               OR ',
'               bron =:P24_BRON',
'             )',
'       AND   ( :P24_TE_LAAT IS NULL',
'               OR ',
'               te_laat =:P24_TE_LAAT',
'             )',
'       AND   ( :P24_DEADLINE IS NULL',
'               OR ',
'               deadline =:P24_DEADLINE',
'             )',
'       AND   ( :P24_MAATREGEL IS NULL',
'               OR ',
'               obw_id =:P24_MAATREGEL',
'             )',
'      AND    ( :P24_PROCES_ID IS NULL',
'               OR',
'               INSTR(obw_pcs_ids,''D''||:P24_PROCES_ID||''D'') > 0',
'               OR',
'               INSTR(dct_pcs_ids,''D''||:P24_PROCES_ID||''D'') > 0',
'               OR',
'               INSTR(bvg_pcs_ids,''D''||:P24_PROCES_ID||''D'') > 0',
'               OR',
'               cte_pcs_id = :P24_PROCES_ID',
'             )',
'     )',
'WHERE ( :P24_RAI_ID_EIGENAAR IS NULL',
'        OR    ',
'        :P24_RAI_ID_EIGENAAR = eigenaar',
'      )',
'AND   ( :P24_RAI_ID_BEHEERDER IS NULL',
'        OR    ',
'        :P24_RAI_ID_BEHEERDER = beheerder',
'      )',
'GROUP BY  ',
'       bron',
',      uitvoerder',
',      eigenaar',
',      beheerder',
',      jaar',
',      status',
',      acties',
''))
