,p_plug_display_column=>1
,p_plug_display_point=>'BODY_3'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT id',
',        omschrijving_actie  -- substr(omschrijving_actie,1,100)',
',      ''<span title="''||herkomst||''">''||substr(herkomst,1,100)||''</span>''',
'  herkomst',
',      DECODE(NVL(obw_id,-1),-1,sms_pck.bepaal_doc_eigenaar(dct_id),sms_pck.bepaal_obw_eigenaar(obw_id)) eigenaar',
',      DECODE(NVL(obw_id,-1),-1,sms_pck.bepaal_doc_beheerder(dct_id),sms_pck.bepaal_obw_beheerder(obw_id)) beheerder',
',type',
',      DECODE(type',
'       ,''BEVINDING'',''<a href=''||CHR(34)||''f?p=&APP_ID.:89:&APP_SESSION.::NO:89,RIR:IREQ_ID:''||BVG_ID||CHR(34)||''>''||type||''</a>''',
'       ,''CONTROLE'', ''<a href=''||CHR(34)||''f?p=&APP_ID.:19:&APP_SESSION.::NO:19,RIR:IREQ_ID:''||cte_id||CHR(34)||''>''||type||''</a>''',
'       ,''DOCUMENT'', ''<a href=''||CHR(34)||''f?p=&APP_ID.:7:&APP_SESSION.::NO:7,RIR:IREQ_ID:''||dct_id||CHR(34)||''>''||type||''</a>''',
'        ) type_link',
',      prioriteit',
',      uitvoerder',
',      cte_id',
',      bvg_id',
',      dct_id',
',      datum_uitvoering',
',      Jaar',
',      datum_rapportage',
',      status',
',       bevindingen  -- substr(bevindingen,1,100)',
',      afwijkingen_indicator',
',      toelichting_afwijking   --substr(toelichting_afwijking,1,100) ',
',      callnr',
',      updated_by',
',      update_date',
',      creation_date',
',      created_by',
',      autorisatie',
',      FILENAAM       ',
',      MIMETYPE       ',
',      DATUM_FILE     ',
',      obw_id',
',       afwijking_hersteld  -- substr(afwijking_hersteld,1,100)',
',      sms_pck.maatregel( pin_obw_id => obw_id)  maatregel  --substr(sms_pck.maatregel( pin_obw_id => obw_id),1,100)',
',      doc_content',
',      te_laat',
',      deadline',
'FROM   ( SELECT',
'           t.id',
'         , sms_pck.herkomst_naam(t.cte_id,t.dct_id,t.bvg_id) herkomst',
'         , sms_pck.herkomst_type(t.cte_id,t.dct_id,t.bvg_id) type',
'         , sms_pck.functie_naam(t.rai_id) uitvoerder',
'         , sms_pck.bepaal_maatregel(bvg_id,dct_id,cte_id) obw_id',
'         , t.cte_id',
'         , t.bvg_id',
'         , t.dct_id',
'         , t.datum_uitvoering',
'         , EXTRACT (YEAR FROM t.datum_uitvoering) Jaar',
'         , t.datum_rapportage',
'         , t.status',
'         , t.bevindingen',
'         , t.afwijkingen_indicator',
'         , t.toelichting_afwijking',
'         , t.opmerking omschrijving_actie',
'         , t.callnr',
'         , t.prioriteit',
'         , t.updated_by',
'         , t.update_date',
'         , t.creation_date',
'         , t.created_by',
'         , sms_pck.autorisatie_nummer(t.bvg_id,t.dct_id,t.cte_id,t.rai_id) autorisatie',
'         ,t.FILENAAM       ',
'         ,t.MIMETYPE       ',
'         ,t.DATUM_FILE     ',
'         ,dbms_lob.getlength("DOC_CONTENT") doc_content ',
'         ,      ( SELECT sms_pck.obw_pcs_ids( pin_obw_id => cte.obw_id) pcs_ids',
'                  FROM   sms_controles cte',
'                  WHERE  cte.id = t.cte_id',
'                ) obw_pcs_ids',
'         ,      ( SELECT pcs_id',
'                  FROM   sms_controles cte',
'                  WHERE  cte.id = t.cte_id',
'                ) cte_pcs_id',
'         ,      ( SELECT RTRIM (XMLAGG (XMLELEMENT (e, sms_pck.obw_pcs_ids( pin_obw_id => dow.obw_id) || '','')).EXTRACT (''//text()''), '','')',
'                  FROM   sms_documenten_obw dow',
'                  WHERE  dow.dct_id = t.dct_id',
'                ) dct_pcs_ids',
'         ,      ( SELECT sms_pck.obw_pcs_ids( pin_obw_id => bvg.obw_id) pcs_ids',
'                  FROM   sms_bevindingen bvg',
'                  WHERE  bvg.id = t.bvg_id',
'                ) bvg_pcs_ids',
'         ,      t.afwijking_hersteld_indicator afwijking_hersteld',
'         ,      CASE WHEN t.status != ''Afgerond'' AND datum_uitvoering < SYSDATE THEN ''J'' ELSE ''N'' END te_laat',
'         ,      CASE WHEN t.status != ''Afgerond'' AND ((datum_uitvoering - SYSDATE) BETWEEN 0 AND 7) THEN ''J'' ELSE ''N'' END deadline',
'         FROM sms_acties t',
'       )',
'WHERE ( :P28_PROCES_ID IS NULL',
'        OR',
'        INSTR(obw_pcs_ids,''D''||:P28_PROCES_ID||''D'') > 0',
'        OR',
'        INSTR(dct_pcs_ids,''D''||:P28_PROCES_ID||''D'') > 0',
'        OR',
'        INSTR(bvg_pcs_ids,''D''||:P28_PROCES_ID||''D'') > 0',
'        OR',
'        cte_pcs_id = :P28_PROCES_ID',
'      )',
'',
'',
'',
