,p_plug_display_column=>1
,p_plug_display_point=>'BODY_3'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT mt',
',      tl',
',      COUNT(DISTINCT maatregel) maatregel',
',      SUM(actie) acties',
',      SUM(rapportage) rapportage',
',      SUM(behandeling) behandeling',
',      SUM(nok) nok',
',      SUM(hersteld) hersteld',
',      SUM(bevindingen) bevindingen',
'FROM   ( SELECT obw.maatregel',
'         ,      obw.SBNI_MAATREGELINHOUD',
'         ,      sms_pck.functie_naam (obw.rai_id_eigenaar) mt',
'         ,      sms_pck.functie_naam (obw.rai_id_beheerder) tl',
'         ,      1 actie',
'         ,      DECODE(ate.status,''Afgerond'',1,0) rapportage',
'         ,      DECODE(ate.status,''In behandeling'',1,0) behandeling',
'         ,      DECODE(ate.AFWIJKINGEN_INDICATOR,''J'',1,0) nok',
'         ,      DECODE(ate.AFWIJKING_HERSTELD_INDICATOR,''J'',1,0) Hersteld',
'         ,      ( SELECT COUNT(*)',
'                  FROM   sms_bevinding_acties bae',
'                  WHERE  bae.cae_id = ate.id',
'                  AND    ROWNUM < 2',
'                ) bevindingen',
'         FROM   sms_obw obw',
'         ,      sms_controles cte',
'         ,      sms_acties ate',
'         WHERE  cte.obw_id        = obw.id',
'         AND    ate.cte_id        = cte.id',
'         AND    ( :P62_PROCES_ID IS NULL OR :P62_PROCES_ID IN ( SELECT pcs_id FROM SMS_OBW_PROCESSEN ops WHERE ops.obw_id = obw.id AND ops.pcs_id = :P62_PROCES_ID ))',
'         AND    ( :P62_RAI_ID_UITVOERDER IS NULL OR cte.RAI_ID_RESP_WERKING = :P62_RAI_ID_UITVOERDER )',
'         AND    ate.datum_uitvoering >= NVL(:P62_DATUM_INGANG,ate.datum_uitvoering)',
'         AND    ate.datum_uitvoering <= NVL(:P62_DATUM_EINDE,ate.datum_uitvoering)',
'         UNION ALL',
'         ( SELECT obw.maatregel',
'           ,      obw.SBNI_MAATREGELINHOUD',
'           ,      sms_pck.functie_naam (obw.rai_id_eigenaar) mt',
'           ,      sms_pck.functie_naam (obw.rai_id_beheerder) tl',
'           ,      0 actie',
'           ,      0',
'           ,      0',
'           ,      0',
'           ,      0',
'           ,      0',
'           FROM   sms_obw obw',
'           WHERE  ( obw.datum_ingang <= NVL(:P62_DATUM_EINDE,obw.datum_ingang)  OR obw.datum_ingang IS NULL)',
'           AND    ( obw.datum_einde >= NVL(:P62_DATUM_INGANG,obw.datum_einde)  OR obw.datum_einde IS NULL)',
'           AND    ( :P62_PROCES_ID IS NULL OR :P62_PROCES_ID IN ( SELECT pcs_id FROM SMS_OBW_PROCESSEN ops WHERE ops.obw_id = obw.id AND ops.pcs_id = :P62_PROCES_ID ))',
'           AND    ( :P62_RAI_ID_UITVOERDER IS NULL OR  obw.id IN ( SELECT obw_id FROM sms_controles cte WHERE cte.RAI_ID_RESP_WERKING = :P62_RAI_ID_UITVOERDER ))',
'           MINUS',
'           SELECT obw.maatregel',
'           ,      obw.SBNI_MAATREGELINHOUD',
'           ,      sms_pck.functie_naam (obw.rai_id_eigenaar) mt',
'           ,      sms_pck.functie_naam (obw.rai_id_beheerder) tl',
'           ,      0 actie',
'           ,      0',
'           ,      0',
'           ,      0',
'           ,      0',
'           ,      0',
'           FROM   sms_obw obw',
'           ,      sms_controles cte',
'           ,      sms_acties ate',
'           WHERE  cte.obw_id            = obw.id',
'           AND    ate.cte_id            = cte.id',
'           AND    ate.datum_uitvoering >= NVL(:P62_DATUM_INGANG,ate.datum_uitvoering)',
'           AND    ate.datum_uitvoering <= NVL(:P62_DATUM_EINDE,ate.datum_uitvoering)',
'         )',
'       )',
'GROUP BY mt, tl',
''))
,p_plug_source_type=>'NATIVE_IR'
,p_plug_query_row_template=>1
);
