wwv_flow_api.create_page_plug(
 p_id=>wwv_flow_api.id(18430832891959029264)
,p_plug_name=>'ISO-SOA'
,p_region_template_options=>'#DEFAULT#'
,p_component_template_options=>'#DEFAULT#'
,p_plug_template=>wwv_flow_api.id(23153220512016127539)
,p_plug_display_sequence=>20
,p_include_in_reg_disp_sel_yn=>'N'
,p_plug_new_grid_row=>false
,p_plug_new_grid_column=>false
,p_plug_display_column=>1
,p_plug_display_point=>'BODY_3'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT hoofdstuk',
',      sectie',
',      paragraaf',
',      reden_uitsluiting',
',      selectie_reden',
',      organisatie',
',      regelgeving',
',      COUNT(DISTINCT maatregel) maatregel',
',      SUM(actie) acties',
',      SUM(rapportage) rapportage',
',      SUM(behandeling) behandeling',
',      SUM(nok) nok',
',      SUM(hersteld) hersteld',
',      SUM(bevindingen) bevindingen',
'FROM   ( SELECT obw.maatregel',
'         ,      obw.SBNI_MAATREGELINHOUD',
'         ,      viso.HOOFDSTUK_NR||''-''||viso.HOOFDSTUK_TITEL hoofdstuk',
'         ,      viso.sectie_NR||''-''||viso.sectie_TITEL sectie',
'         ,      viso.paragraaf_NR||''-''||viso.paragraaf_TITEL paragraaf',
'         ,      1 actie',
'         ,      DECODE(ate.status,''Afgerond'',1,0) rapportage',
'         ,      DECODE(ate.status,''In behandeling'',1,0) behandeling',
'         ,      DECODE(ate.AFWIJKINGEN_INDICATOR,''J'',1,0) nok',
'         ,      DECODE(ate.AFWIJKING_HERSTELD_INDICATOR,''J'',1,0) Hersteld',
'         ,      ( SELECT COUNT(*)',
'                  FROM   sms_bevinding_acties bae',
'                  WHERE  bae.cae_id = ate.id',
'                  AND    ROWNUM < 2',
'                ) bevindingen',
'         ,      viso.reden_uitsluiting',
'         ,      viso.selectie_reden',
'         ,      viso.organisatie_ind organisatie',
'         ,      viso.regelgeving',
'          FROM  sms_obw obw',
'          ,     sms_obw_normen onm',
'         ,      sms_controles cte',
'         ,      sms_acties ate',
'         ,      sms_normen nrm',
'         ,      sms_v_iso viso',
'         WHERE  cte.obw_id = obw.id',
'         AND    ( :P68_OGE_ID IS NULL OR obw.OGE_ID = :P68_OGE_ID)         ',
'         AND    ( :P68_ORGANISATIE_IND IS NULL OR viso.organisatie_ind = :P68_ORGANISATIE_IND)',
'         AND    ate.cte_id        = cte.id',
'         AND    onm.nrm_id        = nrm.id',
'         AND    obw.id            = onm.obw_id',
'         AND    nrm.ipf_id        = viso.id',
'         AND    ate.datum_uitvoering >= NVL(:P68_DATUM_INGANG,ate.datum_uitvoering)',
'         AND    ate.datum_uitvoering <= NVL(:P68_DATUM_EINDE,ate.datum_uitvoering)',
'UNION ALL',
'(',
'SELECT obw.maatregel',
'         ,      obw.SBNI_MAATREGELINHOUD',
'         ,      viso.HOOFDSTUK_NR||''-''||viso.HOOFDSTUK_TITEL hoofdstuk',
'         ,      viso.sectie_NR||''-''||viso.sectie_TITEL sectie',
'         ,      viso.paragraaf_NR||''-''||viso.paragraaf_TITEL paragraaf',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      viso.reden_uitsluiting',
'         ,      viso.selectie_reden',
'         ,      viso.organisatie_ind organisatie',
'         ,      viso.regelgeving',
'         FROM   sms_obw obw',
'         ,      sms_normen nrm',
'         ,      sms_obw_normen onm',
'         ,      sms_v_iso viso',
'         WHERE  onm.nrm_id        = nrm.id',
'         AND    ( :P68_OGE_ID IS NULL OR obw.OGE_ID = :P68_OGE_ID)         ',
'         AND    obw.id            = onm.obw_id',
'         AND    ( :P68_ORGANISATIE_IND IS NULL OR viso.organisatie_ind = :P68_ORGANISATIE_IND)',
'         AND    nrm.ipf_id        = viso.id',
'         AND    ( obw.datum_ingang <= NVL(:P68_DATUM_EINDE,obw.datum_ingang)  OR obw.datum_ingang IS NULL)',
'         AND    ( obw.datum_einde >= NVL(:P68_DATUM_INGANG,obw.datum_einde)  OR obw.datum_einde IS NULL)',
'MINUS',
'SELECT obw.maatregel',
'         ,      obw.SBNI_MAATREGELINHOUD',
'         ,      viso.HOOFDSTUK_NR||''-''||viso.HOOFDSTUK_TITEL hoofdstuk',
'         ,      viso.sectie_NR||''-''||viso.sectie_TITEL sectie',
'         ,      viso.paragraaf_NR||''-''||viso.paragraaf_TITEL paragraaf',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      viso.reden_uitsluiting',
'         ,      viso.selectie_reden',
'         ,      viso.organisatie_ind organisatie',
'         ,      viso.regelgeving',
'         FROM   sms_obw obw',
'         ,      sms_controles cte',
'         ,      sms_acties ate',
'         ,      sms_normen nrm',
'         ,      sms_obw_normen onm',
'         ,      sms_v_iso viso',
'         WHERE  cte.obw_id = obw.id',
'         AND    ( :P68_ORGANISATIE_IND IS NULL OR viso.organisatie_ind = :P68_ORGANISATIE_IND)',
'         AND    ate.cte_id        = cte.id',
'         AND    onm.nrm_id        = nrm.id',
'         AND    obw.id            = onm.obw_id',
'         AND    ( :P68_OGE_ID IS NULL OR obw.OGE_ID = :P68_OGE_ID)         ',
'         AND    nrm.ipf_id        = viso.id',
'         AND    ate.datum_uitvoering >= NVL(:P68_DATUM_INGANG,ate.datum_uitvoering)',
'         AND    ate.datum_uitvoering <= NVL(:P68_DATUM_EINDE,ate.datum_uitvoering)',
')',
'UNION ALL',
'(',
'SELECT          NULL',
'         ,      NULL',
'         ,      viso.HOOFDSTUK_NR||''-''||viso.HOOFDSTUK_TITEL hoofdstuk',
'         ,      viso.sectie_NR||''-''||viso.sectie_TITEL sectie',
'         ,      viso.paragraaf_NR||''-''||viso.paragraaf_TITEL paragraaf',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      0',
'         ,      viso.reden_uitsluiting',
'         ,      viso.selectie_reden',
'         ,      viso.organisatie_ind organisatie',
'         ,      viso.regelgeving',
'         FROM   sms_normen nrm',
'         ,      sms_v_iso viso',
'         WHERE  nrm.ipf_id        = viso.id',
'         AND    ( :P68_ORGANISATIE_IND IS NULL OR viso.organisatie_ind = :P68_ORGANISATIE_IND)',
'         AND    NOT EXISTS ( SELECT ''x'' FROM sms_obw_normen onm1 WHERE onm1.nrm_id = nrm.id)',
')',
'       )',
'GROUP BY ',
'       hoofdstuk',
',      sectie',
',      paragraaf',
',      reden_uitsluiting',
',      selectie_reden',
',      organisatie',
',      regelgeving',
'',
''))
,p_plug_source_type=>'NATIVE_IR'
,p_plug_query_row_template=>1
);
