',      SUM(AANTAL) totaal',
',      SUM(open) open',
',      SUM(te_laat) te_laat',
',      SUM(afgekeurd_ib) afgekeurd_ib',
',      SUM(deadline) week_deadline',
',      SUM(gereed_met_ib) gereed_met_ib',
',      obw_eigenaar MT',
',      obw_beheerder TL',
'FROM   ( SELECT CASE WHEN bvg.review_ind=''O'' AND bvg.gereed_ind=''J'' THEN 1 ELSE 0 END gereed',
'         ,      CASE WHEN bvg.datum_gereed < SYSDATE AND bvg.gereed_ind=''N'' THEN 1 ELSE 0 END te_laat',
